mapper.select:
select

from TSYS_USER
where 1=1

  and USER_ID=#{userId}


    and USER_NAME like  concat(concat('%',#{userName}),'%')


  and USER_PWD=#{userPwd}


  and TENANT_ID = #{tenantId}


  and ORG_ID = #{orgId}


  and USER_STATUS = #{userStatus}

order by CREATE_DATE DESC

select count(*) from TSYS_USER
where 1=1

    and USER_ID=#{userId}


    and USER_NAME like  concat(concat('%',#{userName}),'%')


    and USER_PWD=#{userPwd}


    and TENANT_ID = #{tenantId}


    and ORG_ID = #{orgId}


    and USER_STATUS = #{userStatus}
